def temperatureConverter(tipoDeGradosRecibidos: str):
    global grados, gradosConvertidos
    grados = game.ask_for_number("Grados")
    if tipoDeGradosRecibidos == "C":
        gradosConvertidos = grados * 9 / 5 + 32
        gradosConvertidosRounded = Math.round(gradosConvertidos)
        game.show_long_text("Los " + ("" + str(grados)) + "ºC són: " + ("" + str(gradosConvertidosRounded)) + "ºF",
            DialogLayout.BOTTOM)
    if tipoDeGradosRecibidos == "F":
        gradosConvertidos = (grados - 32) * 5 / 9
        gradosConvertidosRounded = Math.round(gradosConvertidos)
        game.show_long_text("Los " + ("" + str(grados)) + "ºF són: " + ("" + str(gradosConvertidosRounded)) + "ºC",
            DialogLayout.BOTTOM)
gradosConvertidos = 0
grados = 0
tipoDeGrados = 0
grados = 0
